  //       this.setState({
  //         username: res.data.username,
  //         authenticated: res.data.authenticated
  //       });
  //     })
  //     .catch(error => {
  //       console.log(error);
  //     });
  // }



  render() {
      return (
        <nav className="navbar sticky-top navbar-light bg-light">
            <LoggedIn/>
        </nav>
      );
    }
  }

  export default AppNavBar;
